import React, { useState, useCallback, useMemo } from 'react';

import './App.css';
import DemoList from './components/Demo/DemoList';
import Button from './components/UI/Button/Button';

function App() {
  const [listTitle, setListTitle] = useState('My List');
  const [isDescending, setIsDescending] = useState(false); // State to track sort order

  const changeTitleHandler = useCallback(() => {
    setListTitle('New Title');
  }, []);

  const toggleSortOrder = useCallback(() => {
    setIsDescending((prevIsDescending) => !prevIsDescending); // Toggle the sort order
  }, []);

  // Adjust listItems useMemo to sort based on isDescending state
  const listItems = useMemo(() => {
    const items = [5, 3, 1, 10, 9];
    return isDescending ? items.sort((a, b) => b - a) : items.sort((a, b) => a - b);
  }, [isDescending]);

  return (
    <div className="app">
      <DemoList title={listTitle} items={listItems} />
      <Button onClick={changeTitleHandler}>Change List Title</Button>
      <Button onClick={toggleSortOrder}>Change to Descending Order</Button> {/* New button to toggle sort order */}
    </div>
  );
}

export default App;
