import React, { useState, useCallback, useMemo } from 'react';
import './App.css';
import DemoList from './components/Demo/DemoList';
import Button from './components/UI/Button/Button';

function App() {
  const [listTitle, setListTitle] = useState('My List');
  // Initialize isAscending to true for ascending order initially
  const [isAscending, setIsAscending] = useState(true);

  const changeTitleHandler = useCallback(() => {
    setListTitle('New Title');
  }, []);

  // This handler toggles between ascending and descending order
  const toggleSortOrderHandler = useCallback(() => {
    setIsAscending((prevIsAscending) => !prevIsAscending);
  }, []);

  // useMemo to sort the list based on isAscending state
  const listItems = useMemo(() => {
    // Copy the array before sorting to avoid mutating original array
    const itemsCopy = [5, 3, 1, 10, 9];
    // Sort based on isAscending state
    return isAscending ? itemsCopy.sort((a, b) => a - b) : itemsCopy.sort((a, b) => b - a);
  }, [isAscending]);

  return (
    <div className="app">
      <DemoList title={listTitle} items={listItems} />
      <Button onClick={changeTitleHandler}>Change List Title</Button>
      {/* Button to toggle sort order */}
      <Button onClick={toggleSortOrderHandler}>
        Change to {isAscending ? "Descending" : "Ascending"} Order
      </Button>
    </div>
  );
}

export default App;
